const getUserRecommendedEvents = `-- name: GetUserRecommendedEvents :many
WITH user_tags AS (
    SELECT (tag->>'id')::INT AS tag_id
    FROM user_with_tags_view,
         json_array_elements(tags) AS tag
    WHERE user_with_tags_view.id = $3
)
SELECT
    evt.id,
    evt.name,
    evt.description,
    evt.capacity,
    evt.latitude,
    evt.longitude,
    evt.address,
    evt.date,
    evt.owner_id,
    evt.owner_username,
    evt.is_private,
    evt.is_premium,
    evt.created_at,
    evt.tags,
    evt.participants_count,
    evt.event_image_path,
    evt.user_image_path,
    (
        SELECT COUNT(*)
        FROM event_tags et
        WHERE
            et.event_id = evt.id
          AND et.tag_id IN (SELECT tag_id FROM user_tags)
    ) AS matched_tags,
    ST_Distance(
            evt.geom,
            ST_MakePoint($1::numeric, $2::numeric)::GEOGRAPHY
    ) AS distance
FROM event_with_tags_view evt
WHERE
    evt.date > NOW()
  AND ST_DWithin(
        evt.geom,
        ST_MakePoint($1::numeric, $2::numeric)::GEOGRAPHY,
        100000
      )
  AND evt.is_private = false
ORDER BY
    matched_tags DESC,
    created_at DESC,
    distance ASC
    LIMIT 6
`

