const listAll = `-- name: ListAll :many
SELECT
    evt.id,
    evt.name,
    evt.description,
    evt.capacity,
    evt.latitude,
    evt.longitude,
    evt.address,
    evt.date,
    evt.owner_id,
    evt.owner_username,
    evt.is_private,
    evt.is_premium,
    evt.created_at,
    evt.tags,
    evt.participants_count,
    evt.event_image_path,
    evt.user_image_path,
    (
        SELECT COUNT(*)
        FROM event_tags et
        WHERE et.event_id = evt.id
          AND et.tag_id = ANY($1::int[])
    ) AS matched_tags,
    NULL::float AS distance
FROM event_with_tags_view evt
WHERE
    evt.date > NOW()
  AND (
    $2::text = ''
        OR (
            evt.name ILIKE '%' || $2 || '%'
            OR evt.description ILIKE '%' || $2 || '%'
        )
    )
  AND (
    cardinality($1::int[]) = 0
        OR EXISTS (
        SELECT 1
        FROM event_tags et
        WHERE et.event_id = evt.id
          AND et.tag_id = ANY($1::int[])
    )
    )
  AND (
    $3::text IS NULL
        OR $3::text = ''
        OR CASE
            WHEN $3 = 'day' THEN evt.date BETWEEN NOW() AND NOW() + INTERVAL '1 day'
            WHEN $3 = 'week' THEN evt.date BETWEEN NOW() AND NOW() + INTERVAL '7 days'
            WHEN $3 = 'month' THEN evt.date BETWEEN NOW() AND NOW() + INTERVAL '1 month'
            ELSE TRUE
        END
    )
ORDER BY
    CASE WHEN $2::text <> '' THEN
             SIMILARITY(evt.name, $2) +
             SIMILARITY(evt.description, $2)
         ELSE 0 END DESC,
    matched_tags DESC,
    evt.created_at DESC
`

